import logo from './logo.svg';
import './App.css';
import { useEffect, useState } from 'react';

function App() {
  const [state,setState] =useState({
    items: [],
    DataisLoaded: false
});
const fetchData=()=>{
  fetch("https://jsonplaceholder.typicode.com/users")
                .then((res) => res.json())
                .then((json) => {
                  setState({items: json,
                        DataisLoaded: true
                    });
                })
              }
// useEffect=(()=>{
//   fetchData();
// },[])


const { DataisLoaded, items } = state;
if (!DataisLoaded) return <div> <button onClick={fetchData}> Pleses wait some time.... </button> </div> 
  return (
    <div className="App" style={{color:"black"}}>
      <header className="App-header">
      <h1> Fetch data from an api in react </h1>
        <table>
       {
                items.map((item) => ( 
                <tr key = { item.id } >
                  <td> { item.id }</td>
                  <td>{ item.username }</td>
                    User_Name: { item.username }, 
                    Full_Name: { item.name }, 
                    User_Email: { item.email } 
                    </tr>
                ))
            }
      </table>
      </header>

    </div>
  );
}

export default App;
