import React, { useRef } from 'react'
import { useState } from 'react';

const Task2 = () => {
    const refinput=useRef()
    const [state,setState] =useState({
        items: [],
        DataisLoaded: false,
        username:""
    });
    const fetchData=()=>{
  let rl='https://api.github.com/users/'+refinput.current.value;
      fetch(rl)
                    .then((res) => res.json())
                    .then((json) => {
                      setState({items: json,
                            DataisLoaded: true
                        });
                    })
                  }
                  console.log(state.items)
    const { DataisLoaded, items } = state;
    if (!DataisLoaded) return( 
      <>
      <div className="App-header">
        <input type="text" ref={refinput} name='username'/>
       <button className="App-btn" onClick={fetchData}>Get data</button> 
       </div> 

      </>

    )
    return (
        <div className="App" style={{color:"black"}}>
          <header className="App-header">
               <h3> Fetch data from an api in react </h3>
            <table className="styled-table">
              <thead>
                <tr>
                <td colSpan={2}>Get Details</td>
                </tr>

              </thead>
              <tbody>
           {
            Object.keys(items).forEach(keys => {
            return  <tr key={keys}><td>{keys}</td><td>{items[keys]}</td></tr>
              })
                }
                </tbody>
          </table>
          </header>

        </div>
      );
}

export default Task2
